import React, { Component } from 'react';
import Card from '../persentational/Card';
import Row from '../persentational/Row';
import Col from '../persentational/Col';
import { FormattedMessage } from 'react-intl';
// import FormInput from '../persentational/FormInput';
class SerchForm extends Component {

    state = {
        waybillnumber: undefined
    }

    onChange(e) {
        const value = e.target.value
        this.setState({ waybillnumber: value })
    }
    render() {
        return (
            <Row>
                <Col md={6} lg={6} >
                    <Card
                        cardTitle={
                            <FormattedMessage
                                id={"track shipment"}
                                defaultMessage={"track shipment"}
                            />
                        }
                        cardClass=" "
                        cardBodyClass="myap-p-t-0"
                    >
                        <div className="form-group row ">
                            <label
                                htmlFor="fname"
                                className="col-sm-3 col-md-2 text-right control-label"
                            >
                                waybillnumber
                            </label>
                            <div className="col-sm-9  col-md-6 ">
                                <input

                                    type={Text}
                                    className="form-control"
                                    id={"textid"}
                                    placeholder={"placeholder"}
                                    onChange={(e) => this.onChange(e)}
                                />

                            </div>
                            <button>
                                search
                            </button>
                        </div>

                    </Card>
                </Col>
            </Row>
        );
    }
}

export default SerchForm;
