import React, { Component } from 'react';
import Wrapper from './components/persentational/Wrapper';
// import Content from './components/persentational/Content';
import { Switch, Route, withRouter } from 'react-router-dom';
import Breadcrumblayout from './components/persentational/Breadcrumblayout';
import ShipperStatus from './components/ShipperStatus';
import logo from './logo.svg';
import './App.css';
import './css/c3.min.css'
import './css/chartist.min.css'
import './css/style.min.css'
import './css/jquery-jvectormap-2.0.2.css'
import './css/font-awesome.min.css'
import './App.css';
import { FormattedMessage } from 'react-intl';
import Card from './components/persentational/Card'
import Row from './components/persentational/Row'
import Col from './components/persentational/Col';

class App extends Component {

  state = {
    waybillNumber: undefined

  }
  componentWillMount() {
    console.log("componentWillMount", this);
    let x = this.props.location.pathname
    console.log(x);
    if (this.props.location.pathname) {
        let str = this.props.location.pathname
        let res = str.split("/");
        let len = res.length
        let test = len - 1
        if ((res[test].length > 0)) {
            this.setState({ waybillNumber: res[test] })
        }
    }
}


  onChange(e) {
    // waybillNumber = e.target.value
    this.setState({ waybillNumber: e.target.value }, this.getData)
}
submit = (e) => {
    e.preventDefault()
    this.getData()
}

  render() {
    const {waybillNumber}=this.state
    return (
      <Switch>
        <Route path="/"
          render={() =>
            <Wrapper sidebartype={'full'} >
              <div className="page-wrapper them">
                <Breadcrumblayout
                />

                <div className="container-fluid">

                  <div class="container px-lg-5 ">

                    <div className="row">
                      <Col className="container" sm={12} >
                        <Card
                          cardTitle={
                            <FormattedMessage
                              id={"trackShipment"}
                              defaultMessage={"trackShipment"}
                            />
                          }
                          cardClass=" "
                          cardBodyClass="myap-p-t-0"
                        >
                          <form className="form-group row" onSubmit={this.submit}>
                            <label
                              htmlFor="fname"
                              className="col-sm-3 col-md-3 text-right control-label"
                            >
                              <FormattedMessage
                                id={"waybillNumber"}
                                defaultMessage={"waybillNumber"}
                              />

                            </label>
                            <div className="col-sm-12  col-md-7 ">
                              <input
                                type={"text"}
                                className="form-control"
                                id={"textid"}
                                placeholder={"placeholder"}
                                onChange={(e) => this.onChange(e)}
                                value={waybillNumber}
                                required
                              />

                            </div>

                            <div className="col-sm-12  col-md-2 ">
                              <button
                                type="submit"
                                className="btn btn-info"
                              >

                                <i className="ti-search">
                                </i>
                              </button>
                            </div>

                          </form>
                        </Card>
                      </Col>
                    </div>



                    <ShipperStatus />
                  </div>
                </div>

              </div>
            </Wrapper>
          }
        >
        </Route>

      </Switch>
    );
  }
}
export default withRouter(App);

